import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import App from './App';
import reportWebVitals from './reportWebVitals';


const DarkMode = () => {
  const [state, setState] = useState(true);

  function onToggle() {

      if (state == true) {
          setState(false)
      } else setState(true)
  }
  if (state === true) {
    return ( <div>
        <button className="dark" onClick={onToggle}>DARK MODE</button>
         </div>) 
} 
else return ( <div>
    <button className="light" onClick={onToggle}>LIGHT MODE</button>
     </div>) 
 } 

ReactDOM.render(
  <React.StrictMode>

    <App title="Search by Github username" />
  </React.StrictMode>,
  document.getElementById('root')
);

// If you want to start measuring performance in your app, pass a function
