/*import React, { useState } from 'react';


 const DarkMode = () => {
    const [state, setState] = useState(true);

    function onToggle() {

        if (state == true) {
            setState(false)
        } else setState(true)
    }
if (state === true) {
    return ( <div>
        <button className="dark" onClick={onToggle}>DARK MODE</button>
         </div>) 
} 
else return ( <div>
    <button className="light" onClick={onToggle}>LIGHT MODE</button>
     </div>) 
 } 


    export default DarkMode  */
